mapper.select:
select userNickname
from TBL_USER
where userNickname = #{userNickname}

select userId
from TBL_USER
where userId = #{userId}

update TBL_LOGIN
set LOGINSTATE = 2, LOGOUTTIME = now()
where userId = #{userId}

mapper.update:
update TBL_LOGIN
set LOGINSTATE = 1, LOGINCONNECTCODE = 1, LOGINTIME = now()
where userId = #{userId}

update TBL_LOGIN
set LOGINSTATE = 1, LOGINCONNECTCODE = 1, LOGINTIME = now()
where userId = #{userId}

update TBL_LOGIN
set LOGINSTATE = 1, LOGINCONNECTCODE = 2, LOGINTIME = now()
where userId = #{userId}

update TBL_LOGIN
set LOGINSTATE = 2, LOGOUTTIME = now()
where userId = #{userId}

mapper.select:
update TBL_USER
set userPw =
#{userPw}, userNickname = #{userNickname}
where userId = #{userId}

select LOGINCONNECTCODE
from TBL_LOGIN
where USERID = #{userId}
